/**
 * Analyzes message text to determine intent, risks, and appropriate response style
 * @param {string} messageText - The message to analyze
 * @returns {QueryIntent} Detected intent characteristics
 */
function analyzeQueryIntent(messageText) {
  if (!messageText || typeof messageText !== 'string') {
    return {
      isHighRisk: false,
      isMentalHealthCrisis: false,
      isOffTopic: false,
      containsPII: false,
      isProfessionalQuery: false,
      isSymptomBased: false,
      isChronicCondition: false,
      isPreventionFocused: false,
      hasColdExtremities: false
    };
  }

  const lowerMessage = messageText.toLowerCase();

  // Check for high-risk medical terms
  const isHighRisk = HIGH_RISK_TERMS.some(term => lowerMessage.includes(term));

  // Check for mental health crisis indicators
  const isMentalHealthCrisis = MENTAL_HEALTH_CRISIS_TERMS.some(term => lowerMessage.includes(term));

  // Check for professional medical terminology
  const isProfessionalQuery = PROFESSIONAL_MEDICAL_TERMS.some(term => lowerMessage.includes(term));

  // Check for personal identifying information
  const containsPII = PII_PATTERNS.some(pattern => pattern.test(messageText));

  // Check for off-topic or inappropriate requests
  const isOffTopic = OFF_TOPIC_PATTERNS.some(pattern => pattern.test(messageText));

  // Heuristic for symptom-based vs informational queries
  // Symptom-based queries often start with "I have", "I am feeling", etc.
  const isSymptomBased = /\b(i ('m|am|have|feel|experiencing|suffer|got)|my)\b/i.test(lowerMessage);

  // Check for chronic condition management queries
  const isChronicCondition = CHRONIC_CONDITION_TERMS.some(term => lowerMessage.includes(term));

  // Check for prevention-focused health queries
  const isPreventionFocused = PREVENTION_TERMS.some(term => lowerMessage.includes(term));

  // Define body parts list for symptom location detection
  const bodyParts = [
    'head', 'neck', 'chest', 'back', 'arm', 'arms', 'hand', 'hands', 'finger', 'fingers', 
    'leg', 'legs', 'knee', 'knees', 'ankle', 'ankles', 'foot', 'feet', 'toe', 'toes', 
    'shoulder', 'shoulders', 'elbow', 'elbows', 'wrist', 'wrists', 'hip', 'hips', 
    'stomach', 'abdomen', 'throat', 'ear', 'ears', 'eye', 'eyes', 'nose', 'mouth'
  ];

  // Identify symptom location
  let symptomLocation = '';
  for (const part of bodyParts) {
    if (lowerMessage.includes(part)) {
      symptomLocation = part;
      break;
    }
  }

  // Define symptom types
  const symptomTypes = {
    temperature: ['cold', 'hot', 'warm', 'cool', 'freezing', 'chilly', 'heat'],
    pain: ['pain', 'ache', 'hurt', 'sore', 'tender', 'discomfort', 'burning'],
    skin: ['rash', 'itch', 'swelling', 'bump', 'red', 'pale', 'blue', 'color'],
    sensation: ['numb', 'tingle', 'prickle', 'pins and needles', 'sensitive'],
    motion: ['stiff', 'weak', 'difficult', 'move', 'mobility', 'frozen', 'locked']
  };

  // Identify symptom type
  let symptomType = '';
  for (const [type, terms] of Object.entries(symptomTypes)) {
    if (terms.some(term => lowerMessage.includes(term))) {
      symptomType = type;
      break;
    }
  }

  // Special case for cold extremities - enhanced detection
  const coldExtremityTerms = ['cold', 'cool', 'freezing', 'chilly', 'icy', 'frozen'];
  const extremityTerms = ['feet', 'foot', 'hand', 'hands', 'finger', 'fingers', 'toe', 'toes', 
    'leg', 'legs', 'arm', 'arms', 'extremit', 'limb'];

  // Check for both cold terms and extremity terms in the message
  const hasColdTerms = coldExtremityTerms.some(term => lowerMessage.includes(term));
  const hasExtremityTerms = extremityTerms.some(part => lowerMessage.includes(part));

  // Check for phrases indicating cold extremities vs common cold
  const hasColdExtremities = hasColdTerms && hasExtremityTerms && 
    // Negative check for common cold phrases
    !(lowerMessage.includes('common cold') || 
      lowerMessage.includes('have a cold') || 
      lowerMessage.includes('caught a cold') ||
      (lowerMessage.includes('cold') && lowerMessage.includes('flu')));

  // Determine appropriate disclaimer
  let disclaimer = '';
  if (isMentalHealthCrisis) {
    disclaimer = MENTAL_HEALTH_DISCLAIMER;
  } else if (isHighRisk) {
    disclaimer = EMERGENCY_DISCLAIMER;
  } else if (isOffTopic) {
    disclaimer = OFF_TOPIC_MESSAGE;
  } else if (containsPII) {
    disclaimer = PRIVACY_MESSAGE;
  }

  return {
    isHighRisk,
    isMentalHealthCrisis,
    isOffTopic,
    containsPII,
    isProfessionalQuery,
    isSymptomBased,
    isChronicCondition,
    isPreventionFocused,
    hasColdExtremities,
    symptomLocation,
    symptomType,
    disclaimer
  };
}

